import React,{useState} from "react";
import "./carousel.scss";
import ImgComponent from "./imgcomponent";
import i1 from "../../assets/images/";

function Carousel() {
    let carouselArr =[<ImgComponent src={i1}></ImgComponent>, 2, 3, 4, 5];
    const [x, setX] = useState(0);
    const goLeft=()=>{
        x===0?setX(-100*(carouselArr.length -1)):setX(x+100);
    };
    const goRight=()=>{
       x === -100*(carouselArr.length-1) ? setX(0) : setX(x-100);
    };

    return (
        <div className="carousel">
            {
                carouselArr.map((item,index)=>{
                    return(
                        <div 
                        key={index} 
                        className="carouse"
                        style={{transform: 'translateX(${x}%)' }}
                        >
                            {item}
                        </div>
                    );
                })}
                <button id="goLeft" onClick={goLeft} >left</button>

                <buton id="goRight" onClick={goRight} >right</buton>
        </div>
    );
}

export default Carousel;
